        "$:/plugins/twpub/153846e997d940b6/text/000000372": {
            "role": "text",
            "title": "$:/plugins/twpub/153846e997d940b6/text/000000372",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-153846e997d940b6-f15030 twpub-153846e997d940b6-9882b5\"><div class=\"quote\"><p class=\"extract\"><span>To the Manus native the world is a great platter, curving upwards on all sides, from his flat lagoon village where the pile-houses stand like long-legged birds, placid and unstirred by the changing tides.</span></p></div></div>"
        },
        "$:/plugins/twpub/153846e997d940b6/text/000000373": {
