            "text": "<div class=\"twpub-153846e997d940b6-f15030 twpub-153846e997d940b6-9882b5\"><div class=\"quote\"><p class=\"right1\"><span>—<i class=\"calibre9\">M<span class=\"smallcaps2\">ARGARET</span> M<span class=\"smallcaps2\">EAD</span></i>, G<span class=\"smallcaps2\">ROWING</span> U<span class=\"smallcaps2\">P IN</span> N<span class=\"smallcaps2\">EW</span> G<span class=\"smallcaps2\">UINEA</span></span></p></div></div>"
        },
        "$:/plugins/twpub/153846e997d940b6/text/000000375": {
