        "$:/plugins/twpub/157bb1994db7e256/text/000001201": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001201",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\">\n<a class=\"calibre5\"></a>\n<span class=\"calibre8\"></span>\n</div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001202": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001202",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001203": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001203",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“</i>INSPIRED <i class=\"calibre12\">is the authority on how<span class=\"calibre13\"></span> to build a product that customers actually want. It's not about hiring product managers ‐ it's about establishing a culture that puts the user first, and builds the organization and teams around that customer to ensure that you are building the best product possible. From CEOs to APMs, this is required reading.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001204": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001204",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001205": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001205",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Amanda Richardson, Chief Data and Strategy Officer, HotelTonight</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001206": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001206",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001207": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001207",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001208": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001208",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“We first started working with Marty when ImmobilienScout was entering growth‐stage, and he helped us set the organization up to rapidly scale and grow to become one of the largest and most successful technology startups in Germany. He remained a friend and advisor to the company for many years. His book</i> INSPIRED <i class=\"calibre12\">helped people from all across the company, and the new version is sure to help many more companies.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001209": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001209",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001210": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001210",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Jürgen Böhm, Co‐Founder, Immobilien Scout GmbH</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001211": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001211",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001212": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001212",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001213": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001213",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“It does not matter if you are a seasoned product leader or a new product manager,</i> INSPIRED <i class=\"calibre12\">will make you realise that you have the best job in the world and can have incredible impact ‐ especially if you follow Marty Cagan's words of wisdom. His book has been the bible of our industry for the past decade, and it will no doubt continue to be so with this latest update containing the most exciting best‐in‐class product practices.</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001214": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001214",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001215": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001215",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Tanya Cordrey, former Chief Digital Officer at Guardian News &amp; Media</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001216": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001216",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001217": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001217",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001218": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001218",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“Building a great product that nails Product/Market Fit is always a key first step to any successful start‐up. However, organizing the product and engineering teams, in ways that ensures scalability, speed, and quality is usually the next biggest challenge. Marty's insights and lessons learned can be applied to build highly productive teams to manage through dependencies, and build a culture that is positioned to scale. This applies whether your business is in need of a serious course correction, or on a rocket ship.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001219": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001219",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001220": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001220",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Scott Sahadi, Founder and CEO, The Experience Engine</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001221": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001221",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001222": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001222",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001223": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001223",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“Marty offers actionable advice on<span class=\"calibre13\"></span> product management without being too prescriptive, making his wisdom applicable in many contexts. He draws from a wealth of experience, illustrating his advice with dozens of real‐world stories. If you want to create digital products that people love, this book will get you started on the right path.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001224": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001224",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001225": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001225",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Teresa Torres, Discovery Coach</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001226": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001226",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001227": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001227",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001228": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001228",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“We have worked closely with Marty shaping product and building product management organisations in several of our portfolio companies. Marty's insight and advice is leading‐edge and world‐class.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001229": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001229",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001230": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001230",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Harry Nellis, Partner, Accel</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001231": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001231",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
