        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001237": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001237",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001238": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001238",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“I've been lucky to work with some of the best product managers and product minds in the business. In my experience Marty Cagan is hands down the absolute best product management mind alive today. This book packs years of experience into 250 pages.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001239": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001239",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001240": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001240",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Marty Abbott, CEO, AKF Partners, former CTO, eBay</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001241": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001241",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001242": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001242",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001243": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001243",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“Great products delight customers. Marty Cagan has led and inspired countless product teams and in</i> INSPIRED<i class=\"calibre12\">, you will learn how to build those products, both strategically and tactically.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001244": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001244",
