        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001245": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001245",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"right\">—Shripriya Mahesh, Partner, Omidyar Network</p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001246": {
