        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001302": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001302",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\">\n</blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001303": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001303",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-157bb1994db7e256-d59a78 twpub-157bb1994db7e256-9882b5\"><blockquote class=\"extract\"><p class=\"calibre11\"><i class=\"calibre12\">“Marty has a way of elegantly simplifying decades of experience leading and teaching product organizations to excel in value creation for their customers into one actionable, inspiring, quick read. From organizational assessments, tools for aligning teams against a real user need, to the nitty gritty of pulling off continuous product discovery &amp; delivery,</i> INSPIRED <i class=\"calibre12\">is my go‐to reference and recommendation for any Product Leader looking to up their game for the sake of building winning products.”</i></p></blockquote></div>"
        },
        "$:/plugins/twpub/157bb1994db7e256/text/000001304": {
            "role": "text",
            "title": "$:/plugins/twpub/157bb1994db7e256/text/000001304",
