        },
        "$:/plugins/twpub/3db9df62ce7f9e85/text/000000809": {
            "role": "text",
            "title": "$:/plugins/twpub/3db9df62ce7f9e85/text/000000809",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-3db9df62ce7f9e85-cddbf7\"><p>刘易斯·A．科瑟：《社会学思想名家》，中国社会科学出版社1990年版。</p></div>"
        },
        "$:/plugins/twpub/3db9df62ce7f9e85/text/000000810": {
            "role": "text",
            "title": "$:/plugins/twpub/3db9df62ce7f9e85/text/000000810",
            "type": "text/vnd.twpub",
