        "$:/plugins/twpub/51a210526ab7fa86/text/000000172": {
            "role": "text",
            "title": "$:/plugins/twpub/51a210526ab7fa86/text/000000172",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-51a210526ab7fa86-595ed6 twpub-51a210526ab7fa86-9882b5\"><h2><span style=\"\">第2节：草木染--蓝草(1)</span></h2></div>"
        },
        "$:/plugins/twpub/51a210526ab7fa86/text/000000173": {
            "role": "text",
            "title": "$:/plugins/twpub/51a210526ab7fa86/text/000000173",
