        },
        "$:/plugins/twpub/5d8a027eb7b5e30f/text/000000209": {
            "role": "text",
            "title": "$:/plugins/twpub/5d8a027eb7b5e30f/text/000000209",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-5d8a027eb7b5e30f-30773b twpub-5d8a027eb7b5e30f-9882b5\"><div class=\"sgc-toc-level\"><div class=\"sgc-toc-level1\">\n      <$link to=\"$:/plugins/twpub/5d8a027eb7b5e30f/text/000001180\">第二十四章 费曼技巧：简化原则</$link>\n    </div></div></div>"
        },
        "$:/plugins/twpub/5d8a027eb7b5e30f/text/000000210": {
            "role": "text",
            "title": "$:/plugins/twpub/5d8a027eb7b5e30f/text/000000210",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-5d8a027eb7b5e30f-30773b twpub-5d8a027eb7b5e30f-9882b5\"><div class=\"sgc-toc-level\">\n\n    </div></div>"
        },
        "$:/plugins/twpub/5d8a027eb7b5e30f/text/000000211": {
            "role": "text",
            "title": "$:/plugins/twpub/5d8a027eb7b5e30f/text/000000211",
