        "$:/plugins/twpub/80c4d6e738750d81/text/000001075": {
            "role": "text",
            "title": "$:/plugins/twpub/80c4d6e738750d81/text/000001075",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-80c4d6e738750d81-1e04d0 twpub-80c4d6e738750d81-c60c1f\"><blockquote>\n\n    </blockquote></div>"
