        "$:/plugins/twpub/c69c7142dcf77b93/text/000000925": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000000925",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_10\">\n\t\t\t<td class=\"td_3\"><p class=\"block_326\"><span class=\"text_\">支柱声部</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">支点声部</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">起点</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">终点</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_42\">\n\t\t\t<td rowspan=\"3\" class=\"td_3\"><p class=\"block_326\"><span class=\"text_\">人物声部</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">身份</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">乐团弱者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">有尊严的艺术家</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_9\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">欲望</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_22\">以公谋私</span><span class=\"text_22\"></span><span class=\"text_22\">得过且过</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_22\">捍卫尊严</span><span class=\"text_22\"></span><span class=\"text_22\">发泄愤懑</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_9\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">动作</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_22\">敷衍工作</span><span class=\"text_22\"></span><span class=\"text_22\">恶搞同行</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">恶搞高雅音乐会</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_8\">\n\t\t\t<td rowspan=\"3\" class=\"td_3\"><p class=\"block_326\"><span class=\"text_\">事件声部</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_327\"><span class=\"text_\">核心问题</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">排练能顺利进行吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">乐团能保住吗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_8\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">主要障碍</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_22\">主角捣乱</span><span class=\"text_22\"></span><span class=\"text_22\">人心涣散</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_328\"><span class=\"text_9\">乐团的抗争行为</span><span class=\"text_72\">一</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_9\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">结果</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_22\">排练终止</span><span class=\"text_22\"></span><span class=\"text_22\">不欢而散</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_294\"><span class=\"text_9\">金主愤怒</span><span class=\"text_10\">.</span><span class=\"text_9\">围观者狂欢</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_9\">\n\t\t\t<td rowspan=\"2\" class=\"td_3\"><p class=\"block_326\"><span class=\"text_\">主题声部</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">正价值</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">捍卫艺术尊严</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_52\">\n\t\t\t<td class=\"td_10\"><p class=\"block_145\"><span class=\"text_\">负价值</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_153\"><span class=\"text_22\">金钱至上</span><span class=\"text_22\"></span><span class=\"text_22\">人心浮躁</span></p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
