            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><ol class=\"list_\">\n\t</ol></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000001368": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001368",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><ol class=\"list_\"><li value=\"11\" class=\"block_469\"><span class=\"text_14\">例<span class=\"tab\">      </span></span><span class=\"text_13\">《翻谱</span><span class=\"text_14\">人</span><span class=\"text_34\">》</span><span class=\"text_13\">的幕织体</span></li></ol></div>"
        },
