        "$:/plugins/twpub/c69c7142dcf77b93/text/000001370": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001370",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_470\"><span class=\"text_9\">第一步制</span><span class=\"text_10\">作</span><span class=\"text_9\">“幕织体”工作表</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000001371": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001371",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_471\"><span class=\"text_13\">将</span><span class=\"text_25\">“</span><span class=\"text_13\">幕”单位下</span><span class=\"text_14\">的故事要素置于</span><span class=\"text_25\">“</span><span class=\"text_13\">幕织体”工作表中</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000001372": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001372",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_472\"><span class=\"text_\">《翻谱人》的幕织体工作表</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000001373": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001373",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_72\">\n\t\t\t<td class=\"td_8\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">第一幕</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_156\"><span class=\"text_\">激励 事件</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_\">第二幕上</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_473\"><span class=\"text_\">转折</span></p><p class=\"block_153\"><span class=\"text_\">事件</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">第二幕下</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_473\"><span class=\"text_\">危机</span></p><p class=\"block_153\"><span class=\"text_\">事件</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">第三幕</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_73\">\n\t\t\t<td class=\"td_3\"><p class=\"block_153\"><span class=\"text_\">身份</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_\">乐团边缘人</span></p></td>\n\t\t\t<td rowspan=\"7\" class=\"td_4\"><p class=\"block_474\"><span class=\"text_22\">主角上 </span><span class=\"text_22\">位</span><span class=\"text_22\">领 导救团</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_475\"><span class=\"text_22\">乐团的救 </span><span class=\"text_22\">世主</span></p></td>\n\t\t\t<td rowspan=\"7\" class=\"td_4\"><p class=\"block_476\"><span class=\"text_\">乐团 决定 自行 网络 直播</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_477\"><span class=\"text_\">委曲求全的 艺术商人</span></p></td>\n\t\t\t<td rowspan=\"7\" class=\"td_4\"><p class=\"block_478\"><span class=\"text_\">庆典音 乐会变 成选秀</span></p><p class=\"block_479\"><span class=\"text_\">PK</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_475\"><span class=\"text_22\">有尊严的 </span><span class=\"text_22\">艺术家</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_61\">\n\t\t\t<td class=\"td_3\"><p class=\"block_391\"><span class=\"text_\">欲望</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_22\">利用乐团身 </span><span class=\"text_22\">份个人敛财</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_475\"><span class=\"text_22\">实现乐团 价值</span><span class=\"text_22\">迎 </span><span class=\"text_22\">来新生</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_439\"><span class=\"text_\">不择手段地</span></p><p class=\"block_211\"><span class=\"text_\">救团</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_475\"><span class=\"text_22\">捍卫尊严 </span><span class=\"text_22\">发泄愤懑</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_74\">\n\t\t\t<td class=\"td_3\"><p class=\"block_153\"><span class=\"text_\">动作</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_481\"><span class=\"text_22\">对内敷衍工 作对外坑 </span><span class=\"text_22\">蒙拐骗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_22\">直面资 </span><span class=\"text_22\">本投身 </span><span class=\"text_22\">市场</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_254\"><span class=\"text_22\">放下身段 投身网络直 </span><span class=\"text_22\">播</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">恶搞高雅 音乐会</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_74\">\n\t\t\t<td class=\"td_3\"><p class=\"block_153\"><span class=\"text_\">问题</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_22\">主角和乐团 还混得下去 </span><span class=\"text_22\">吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_482\"><span class=\"text_22\">乐团有价 </span><span class=\"text_22\">值吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_\">乐团能赢来 转机吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_475\"><span class=\"text_22\">乐团能保 </span><span class=\"text_22\">住吗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_61\">\n\t\t\t<td class=\"td_3\"><p class=\"block_153\"><span class=\"text_\">障碍</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_483\"><span class=\"text_22\">金钱至上 </span><span class=\"text_22\">人心涣散</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_484\"><span class=\"text_25\">过度娱</span><span class=\"text_26\">-</span><span class=\"text_25\">乐 </span><span class=\"text_25\">化的市场 </span><span class=\"text_25\">品位</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_485\"><span class=\"text_\">资本制约 职业尊严</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_481\"><span class=\"text_22\">不计后果 的对抗行 </span><span class=\"text_22\">为</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_75\">\n\t\t\t<td class=\"td_3\"><p class=\"block_153\"><span class=\"text_\">结果</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_486\"><span class=\"text_22\">主角风生水 起乐团面 </span><span class=\"text_22\">临散伙</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_486\"><span class=\"text_\">融资无 望票房 惨败</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_485\"><span class=\"text_\">负债累累 进退维谷</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_22\">围观者的 </span><span class=\"text_22\">狂欢</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_76\">\n\t\t\t<td class=\"td_6\"><p class=\"block_153\"><span class=\"text_\">意义</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_475\"><span class=\"text_22\">金钱至上观 念不的艺术 </span><span class=\"text_22\">危局</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_480\"><span class=\"text_22\">艺术价值 的穷途末 </span><span class=\"text_22\">路</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_475\"><span class=\"text_22\">强势金钱面 前的艺术困 </span><span class=\"text_22\">境</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_475\"><span class=\"text_22\">艺术尊严 的悲壮反 </span><span class=\"text_22\">击</span></p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
