        "$:/plugins/twpub/c69c7142dcf77b93/text/000001609": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001609",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_543\"><span class=\"text_\">第一步列出幕内容及核心问题</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000001610": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001610",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_53\">\n\t\t\t<td class=\"td_3\"><p class=\"block_145\"><span class=\"text_\">幕次</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">幕内容</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">核心问题</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_55\">\n\t\t\t<td class=\"td_3\"><p class=\"block_145\"><span class=\"text_\">第一幕</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_19\"><span class=\"text_22\">主角如鱼得水</span><span class=\"text_22\"></span><span class=\"text_22\">乐团面临散伙</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">主角和乐团还混得下去吗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_81\">\n\t\t\t<td class=\"td_3\"><p class=\"block_544\"><span class=\"text_25\">第</span><span class=\"text_26\">二幕</span><span class=\"text_25\"></span><span class=\"text_25\">上</span><span class=\"text_25\"></span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_19\"><span class=\"text_22\">市场救团</span><span class=\"text_22\"></span><span class=\"text_22\">举步维艰</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">乐团有价值吗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_55\">\n\t\t\t<td class=\"td_3\"><p class=\"block_544\"><span class=\"text_25\">第</span><span class=\"text_26\">二幕</span><span class=\"text_25\"></span><span class=\"text_25\">下</span><span class=\"text_25\"></span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_19\"><span class=\"text_22\">网络救团</span><span class=\"text_22\"></span><span class=\"text_22\">柳暗花明</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">乐团能贏来转机吗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_53\">\n\t\t\t<td class=\"td_6\"><p class=\"block_145\"><span class=\"text_\">第三幕</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_19\"><span class=\"text_22\">恶搞高雅</span><span class=\"text_22\"></span><span class=\"text_22\">守卫尊严</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_211\"><span class=\"text_\">乐团能保住吗</span></p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000001611": {
            "role": "text",
