        "$:/plugins/twpub/c69c7142dcf77b93/text/000001645": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000001645",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_11\">\n\t\t\t<td class=\"td_3\"><p class=\"block_145\"><span class=\"text_\">幕次</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">人物</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_563\"><span class=\"text_\">身份</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">欲望</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">动作</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td rowspan=\"5\" class=\"td_3\"><p class=\"block_211\"><span class=\"text_\">第一幕</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">主角</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">乐团边缘人</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">利用乐团个人敛财</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">敷衍工作</span><span class=\"text_22\"></span><span class=\"text_22\">坑蒙拐骗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">指挥</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">无奈的艺术总监</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_422\"><span class=\"text_25\">提振乐团士</span><span class=\"text_26\">气</span><span class=\"text_25\">、</span><span class=\"text_25\">水准</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_564\"><span class=\"text_9\">尽力管</span><span class=\"text_10\">理.</span><span class=\"text_9\">专注业务</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">小提</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">有心无力的乐队队长</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">尽职工作</span><span class=\"text_22\"></span><span class=\"text_22\">提升乐团</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">辅佐指挥团结队员</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_422\"><span class=\"text_25\">小</span><span class=\"text_26\">号</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">随波逐流的主角粉丝</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">向主角学习生财之道</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">敷衍工作</span><span class=\"text_22\"></span><span class=\"text_22\">讨好主角</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_47\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">钢琴</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">骄傲的艺术强人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">表现自我</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_422\"><span class=\"text_25\">目空</span><span class=\"text_26\">一</span><span class=\"text_25\">切</span><span class=\"text_25\"></span><span class=\"text_25\">专注工作</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td rowspan=\"6\" class=\"td_3\"><p class=\"block_556\"><span class=\"text_\">第二幕</span></p><p class=\"block_327\"><span class=\"text_\">(±)</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">主角</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">乐团的救世主</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">实现乐团价值</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_564\"><span class=\"text_9\">寻找投</span><span class=\"text_10\">资.</span><span class=\"text_9\">试水市场</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">指挥</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">坚定的乐团守护者</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">有尊严地保住乐团</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">坚守底线</span><span class=\"text_22\"></span><span class=\"text_22\">积极配合</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_47\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">小提</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">坚定的乐团守护者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">有尊严地保住乐团</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">坚守底线</span><span class=\"text_22\"></span><span class=\"text_22\">积极配合</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">小号</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">信心满满的商场新手</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">借机实现个人价值</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">竭尽所能.配合主角</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">钢琴</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">自私自利的小人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">抛弃乐团</span><span class=\"text_22\"></span><span class=\"text_22\">独自发展</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">权衡利弊</span><span class=\"text_22\"></span><span class=\"text_22\">犹豫进退</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">传总</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">八面玲珑的文化商人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">拓展市场</span><span class=\"text_22\"></span><span class=\"text_22\">做大生意</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">拉拢主角</span><span class=\"text_22\"></span><span class=\"text_22\">共同发展</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td rowspan=\"7\" class=\"td_3\"><p class=\"block_556\"><span class=\"text_\">第一幕</span></p><p class=\"block_145\"><span class=\"text_\">(下)</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">主角</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">委曲求全的艺术商人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">不择手段</span><span class=\"text_22\"></span><span class=\"text_22\">保住乐团</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">投身网络直播</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">指挥</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">忍辱负重的艺术家</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">隐忍退让</span><span class=\"text_22\"></span><span class=\"text_22\">保住乐团</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">放下身段</span><span class=\"text_22\"></span><span class=\"text_22\">努力迎合</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">小提</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">摇摆的乐队队长</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">有尊严地保住乐团</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">左右权衡</span><span class=\"text_22\"></span><span class=\"text_22\">犹豫配合</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_\">小号</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">心灰意懒的失败艺人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">听天由命</span><span class=\"text_22\"></span><span class=\"text_22\">随波逐流</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">得过且过</span><span class=\"text_22\"></span><span class=\"text_22\">机械附和</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">钢琴</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">决绝的背叛者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">摆脱包袱</span><span class=\"text_22\"></span><span class=\"text_22\">另谋生路</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">离开乐团</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_47\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">网总</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">四面楚歌的企业老板</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">转变形象.获取投资</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">积极促成与乐团合作</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">传总</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_440\"><span class=\"text_9\">心</span><span class=\"text_10\">怀不满</span><span class=\"text_9\">的怨妇</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">希望主角悬崖勒马</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">支持主角</span><span class=\"text_22\"></span><span class=\"text_22\">索要回报</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_50\">\n\t\t\t<td rowspan=\"7\" class=\"td_3\"><p class=\"block_427\"><span class=\"text_\">第三幕</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_565\"><span class=\"text_\">主角</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_211\"><span class=\"text_\">有尊严的艺术家</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_565\"><span class=\"text_22\">捍卫尊严</span><span class=\"text_22\"></span><span class=\"text_22\">发泄愤懑</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_463\"><span class=\"text_\">恶搞高雅音乐会</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_47\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">指挥</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">绝望的艺术家</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">捍卫尊严</span><span class=\"text_22\"></span><span class=\"text_22\">拒绝妥协</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">悲壮离开</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_14\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">小提</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">有尊严的艺术家</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">支持主角</span><span class=\"text_22\"></span><span class=\"text_22\">捍卫尊严</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">协同主角</span><span class=\"text_22\"></span><span class=\"text_22\">共同抗争</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_47\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">小号</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">激情重燃的斗士</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">支持主角</span><span class=\"text_22\"></span><span class=\"text_22\">捍卫尊严</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_22\">协同主角</span><span class=\"text_22\"></span><span class=\"text_22\">共同抗争</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_48\">\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">网总</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">扬眉吐气的高雅商人</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_564\"><span class=\"text_9\">利用乐团</span><span class=\"text_10\">.</span><span class=\"text_9\">达成目的</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">打压乐团</span><span class=\"text_22\"></span><span class=\"text_22\">媚俗观众</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_16\">\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">传总</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">心怀仇恨的女人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">报复主角</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">为主角设置各种障碍</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_11\">\n\t\t\t<td class=\"td_10\"><p class=\"block_427\"><span class=\"text_\">芭女</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_211\"><span class=\"text_\">进退两难的竞争者</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_391\"><span class=\"text_22\">战胜对手</span><span class=\"text_22\"></span><span class=\"text_22\">赢得机会</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_391\"><span class=\"text_22\">协助主角</span><span class=\"text_22\"></span><span class=\"text_22\">共同抗争</span></p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
