        "$:/plugins/twpub/c69c7142dcf77b93/text/000002072": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002072",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><ol class=\"list_\"><li value=\"16\" class=\"block_696\"><span class=\"text_14\">例<span class=\"tab\">      </span></span><span class=\"text_13\">《翻谱人</span><span class=\"text_34\">》</span><span class=\"text_13\">的序列织体</span></li></ol></div>"
        },
