        "$:/plugins/twpub/c69c7142dcf77b93/text/000002074": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002074",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_697\"><span class=\"text_13\">第一步制</span><span class=\"text_14\">作</span><span class=\"text_25\">“</span><span class=\"text_13\">序列织体工作表”《翻谱人》的序列织体工作表</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002075": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002075",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_48\">\n\t\t\t<td class=\"td_3\"><p class=\"block_427\"><span class=\"text_\">序列</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">身份</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">欲望</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">动作</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">问题</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">障碍</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">结果</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">意义</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_110\">\n\t\t\t<td class=\"td_3\"><p class=\"block_698\"><span class=\"text_\">01</span></p><p class=\"block_427\"><span class=\"text_\">开篇</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">乐团弱者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_699\"><span class=\"text_9\">以公谋私</span><span class=\"text_10\">. </span><span class=\"text_9\">得过且过</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">敷衍工作 恶搞队友</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">排练能顺 利进行吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">主角捣乱 人心涣散</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">排练不欢 而散</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_22\">金钱至上</span><span class=\"text_22\"> </span><span class=\"text_22\">文化没落</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_31\">\n\t\t\t<td class=\"td_3\"><p class=\"block_425\"><span class=\"text_\">开篇 事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">乐团排练不欢而散</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_94\">\n\t\t\t<td class=\"td_3\"><p class=\"block_700\"><span class=\"text_\">02</span></p><p class=\"block_427\"><span class=\"text_\">切人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">纠结的艺 术骗子</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">骗取金钱 安置心灵</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">亵渎艺术 攫取名利</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">主角能混 得下去吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">道德底线 艺术情结</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">主角捞钱 事业风生 水起</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">艺术沦为 骗取金钱 的幌子</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_110\">\n\t\t\t<td class=\"td_11\"><p class=\"block_701\"><span class=\"text_\">切入</span></p><p class=\"block_427\"><span class=\"text_\">事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">乐团面临解散</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_95\">\n\t\t\t<td class=\"td_3\"><p class=\"block_702\"><span class=\"text_26\">0</span><span class=\"text_25\">3 </span><span class=\"text_25\">走向 激励</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_417\"><span class=\"text_\">全力以赴 的艺术骗 子</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">专心致志 弃艺从商</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_555\"><span class=\"text_\">勾结传总 专心行骗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_703\"><span class=\"text_\">主角何去</span></p><p class=\"block_427\"><span class=\"text_\">何从</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_243\"><span class=\"text_\">乐团需要 主角艺术 情结</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_555\"><span class=\"text_\">主角决定 参与救团</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_704\"><span class=\"text_\">名利取舍 的左右为 难</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_110\">\n\t\t\t<td class=\"td_3\"><p class=\"block_156\"><span class=\"text_22\">激励 </span><span class=\"text_22\">事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">决定救团主角上位</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_95\">\n\t\t\t<td class=\"td_3\"><p class=\"block_705\"><span class=\"text_\">04</span></p><p class=\"block_427\"><span class=\"text_\">进展</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_232\"><span class=\"text_\">心怀叵测 的艺术总 监</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_243\"><span class=\"text_\">借高雅艺 术谋取个 人名利</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_475\"><span class=\"text_22\">为乐团寻 找天使投 </span><span class=\"text_22\">资</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">乐团能找 到投资吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">主角动机 艺术价值</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">无人问津</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_232\"><span class=\"text_\">私利驱动 下的艺术 拯救</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_110\">\n\t\t\t<td class=\"td_11\"><p class=\"block_215\"><span class=\"text_\">进展 事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">决定商演卖票</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_95\">\n\t\t\t<td class=\"td_3\"><p class=\"block_473\"><span class=\"text_\">05</span></p><p class=\"block_427\"><span class=\"text_\">转折</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">有尊严的 乐团管家</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_243\"><span class=\"text_\">全力以赴 实现乐团 价值</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">逐步发力. 争取票房</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">乐团有票 房吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">市场品味 艺术价值</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">演出票房 惨败</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_233\"><span class=\"text_\">艺术价值 的穷途末 路</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_23\">\n\t\t\t<td class=\"td_11\"><p class=\"block_214\"><span class=\"text_\">转折 事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">乐团决定自行网络直播</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_111\">\n\t\t\t<td class=\"td_3\"><p class=\"block_705\"><span class=\"text_\">06</span></p><p class=\"block_145\"><span class=\"text_\">再转</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_704\"><span class=\"text_\">不能自拔 的商业赌 徒</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_485\"><span class=\"text_\">实现高雅 艺术的功 利两全</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_706\"><span class=\"text_\">押上身家 玩命一搏</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_485\"><span class=\"text_\">乐团有流 量吗</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_707\"><span class=\"text_22\">巨额现金 需求</span><span class=\"text_22\">尊 </span><span class=\"text_22\">严底线</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_707\"><span class=\"text_22\">主角与乐 团负债累 </span><span class=\"text_22\">累</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_704\"><span class=\"text_\">一线生机 下的失败 豪赌</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_66\">\n\t\t\t<td class=\"td_17\"><p class=\"block_465\"><span class=\"text_\">再转 事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_10\"><p class=\"block_211\"><span class=\"text_\">接受网总的合作邀请</span></p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
