        "$:/plugins/twpub/c69c7142dcf77b93/text/000002079": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002079",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_50\">\n\t\t\t<td class=\"td_3\"><p class=\"block_391\"><span class=\"text_\">序列</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">身份</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">欲望</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">动作</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">问题</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">障碍</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">结果</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_153\"><span class=\"text_\">意义</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_112\">\n\t\t\t<td class=\"td_3\"><p class=\"block_155\"><span class=\"text_\">07 走向 危机</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_709\"><span class=\"text_\">委曲求全 的失败者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_483\"><span class=\"text_22\">乐团被包 养个人 </span><span class=\"text_22\">止损</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_482\"><span class=\"text_22\">忍气吞声</span><span class=\"text_22\"> </span><span class=\"text_22\">努力迎合</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_418\"><span class=\"text_\">乐团能被</span></p><p class=\"block_19\"><span class=\"text_\">包养吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_483\"><span class=\"text_22\">企业得寸 </span><span class=\"text_22\">进尺</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_391\"><span class=\"text_\">网总变卦</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_483\"><span class=\"text_22\">金钱控制 下的艺术 </span><span class=\"text_22\">屈从</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_12\">\n\t\t\t<td class=\"td_3\"><p class=\"block_419\"><span class=\"text_\">危机</span></p><p class=\"block_391\"><span class=\"text_\">事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_28\"><span class=\"text_25\">庆典音乐会变</span><span class=\"text_26\">成选秀</span><span class=\"text_25\">PK</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_113\">\n\t\t\t<td class=\"td_3\"><p class=\"block_473\"><span class=\"text_\">08</span></p><p class=\"block_419\"><span class=\"text_\">导入</span></p><p class=\"block_419\"><span class=\"text_\">高潮</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_710\"><span class=\"text_\">恪尽职守 的翻谱人</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_482\"><span class=\"text_22\">顾全大局 </span><span class=\"text_22\">达成目标</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_480\"><span class=\"text_22\">做好本职 </span><span class=\"text_22\">工作</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_449\"><span class=\"text_\">乐团能顺 利演出吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_483\"><span class=\"text_22\">主角四面 </span><span class=\"text_22\">楚歌乐 </span><span class=\"text_22\">团人心浮</span></p><p class=\"block_710\"><span class=\"text_\">动</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_482\"><span class=\"text_\">坏事接踵 而至</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_482\"><span class=\"text_22\">尊严全失 的无尽悲 </span><span class=\"text_22\">凉</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_12\">\n\t\t\t<td class=\"td_3\"><p class=\"block_419\"><span class=\"text_\">导入</span></p><p class=\"block_391\"><span class=\"text_\">事件</span></p></td>\n\t\t\t<td colspan=\"7\" class=\"td_4\"><p class=\"block_19\"><span class=\"text_\">情人竟然是对手</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_114\">\n\t\t\t<td class=\"td_3\"><p class=\"block_473\"><span class=\"text_\">09</span></p><p class=\"block_391\"><span class=\"text_\">高潮</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_254\"><span class=\"text_\">有尊严的 艺术家</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_482\"><span class=\"text_22\">捍卫尊严 </span><span class=\"text_22\">发泄愤懑</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_707\"><span class=\"text_22\">率众抗争 恶搞高雅 </span><span class=\"text_22\">音乐会</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_418\"><span class=\"text_\">乐团还保</span></p><p class=\"block_19\"><span class=\"text_\">得住吗</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_254\"><span class=\"text_22\">乐团的抗 </span><span class=\"text_22\">争行为</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_634\"><span class=\"text_25\">网总愤 </span><span class=\"text_32\">怒</span><span class=\"text_26\">.</span><span class=\"text_25\">众人 狂欢</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_483\"><span class=\"text_22\">为了艺术 尊严而奋 </span><span class=\"text_22\">起反击</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_12\">\n\t\t\t<td class=\"td_11\"><p class=\"block_711\"><span class=\"text_\">10</span></p><p class=\"block_391\"><span class=\"text_\">结局</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_19\"><span class=\"text_\">待定</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_40\">\n\t\t\t<td colspan=\"2\" class=\"td_11\"><p class=\"block_712\"><span class=\"text_9\">注为</span><span class=\"text_10\">方便扌</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_28\"><span class=\"text_25\">非</span><span class=\"text_26\">版</span><span class=\"text_25\"></span><span class=\"text_25\">将工</span><span class=\"text_13\"></span></p></td>\n\t\t\t<td colspan=\"5\" class=\"td_9\"><p class=\"block_19\"><span class=\"text_22\">F</span><span class=\"text_22\">表的</span><span class=\"text_11\">X</span><span class=\"text_22\">轴和</span><span class=\"text_11\">Y</span><span class=\"text_22\">轴互换了方向’</span></p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
