        "$:/plugins/twpub/c69c7142dcf77b93/text/000002621": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002621",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_812\"><span class=\"text_25\">注为方便排版</span><span class=\"text_25\"></span><span class=\"text_25\">将工作</span><span class=\"text_26\">表的</span><span class=\"text_13\">X</span><span class=\"text_25\">轴和</span><span class=\"text_13\">Y</span><span class=\"text_25\">轴互换了方向。</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002622": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002622",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_220\"><span class=\"text_13\">笔记</span><span class=\"text_25\"></span><span class=\"text_13\">环节记录就是记录各场景内的每一个环节的</span><span class=\"text_26\">3</span><span class=\"text_13\">环</span><span class=\"text_14\">节名称</span><span class=\"text_13\">、人物动作 和人物对白。<br class=\"calibre7\">\n</span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002623": {
