        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002624": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002624",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><ol class=\"list_\">\n\t</ol></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002625": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002625",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><ol class=\"list_\"><li value=\"21\" class=\"block_630\"><span class=\"text_36\">例<span class=\"tab\">      </span></span><span class=\"text_13\">《翻谱人</span><span class=\"text_34\">》</span><span class=\"text_13\">的环节设计</span></li></ol></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002626": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002626",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><ol class=\"list_\">\n</ol></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002627": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002627",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><p class=\"block_691\"><span class=\"text_25\">《翻谱人</span><span class=\"text_25\">》</span><span class=\"text_25\">环节</span><span class=\"text_26\">工作表</span><span class=\"text_25\">节</span><span class=\"text_26\">选</span><span class=\"text_25\"></span></p></div>"
        },
        "$:/plugins/twpub/c69c7142dcf77b93/text/000002628": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002628",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_50\">\n\t\t\t<td class=\"td_11\"><p class=\"block_153\"><span class=\"text_\">幕</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_19\"><span class=\"text_\">序列</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">场景</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_153\"><span class=\"text_\">环节</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_153\"><span class=\"text_\">动作</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_813\"><span class=\"text_\">对白</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_74\">\n\t\t\t<td rowspan=\"11\" class=\"td_3\"><p class=\"block_642\"><span class=\"text_\">第</span></p><p class=\"block_145\"><span class=\"text_\">幕</span></p></td>\n\t\t\t<td rowspan=\"11\" class=\"td_4\"><p class=\"block_439\"><span class=\"text_\">01</span></p><p class=\"block_211\"><span class=\"text_\">开篇</span></p></td>\n\t\t\t<td rowspan=\"11\" class=\"td_4\"><p class=\"block_557\"><span class=\"text_\">剧场 日 外景 排练</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_814\"><span class=\"text_\">乐队全奏</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_445\"><span class=\"text_\">指挥在激情指挥 乐队在认真演奏 钢琴独奏端坐静候</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_73\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">谱架背后</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_439\"><span class=\"text_\">乐队停钢琴开始演</span></p><p class=\"block_211\"><span class=\"text_\">奏队员玩手机</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_73\">\n\t\t\t<td class=\"td_4\"><p class=\"block_814\"><span class=\"text_\">主角进入</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_445\"><span class=\"text_\">主角进入剧场 指挥的不悦反应</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_73\">\n\t\t\t<td class=\"td_4\"><p class=\"block_814\"><span class=\"text_\">队友互动</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_238\"><span class=\"text_\">小号与主角互动 其他乐手与主角互动</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_123\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">演奏骤停</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_557\"><span class=\"text_\">失败的起奏 小号敲到门牙 指挥拍打谱架</span></p><p class=\"block_557\"><span class=\"text_\">乐手停止演奏</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_73\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">指挥发怒</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_445\"><span class=\"text_\">指挥怒视主角 指挥愤然离场</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_815\"><span class=\"text_\">休息</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_108\">\n\t\t\t<td class=\"td_4\"><p class=\"block_814\"><span class=\"text_\">主角开场</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_238\"><span class=\"text_\">乐手热情期待 主角拿出信息卡片</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_477\"><span class=\"text_22\">控制现场</span><span class=\"text_22\">. </span><span class=\"text_22\">导入内容。</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_108\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">若干艺术商业化环节</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_549\"><span class=\"text_25\">主角</span><span class=\"text_26\">提供信息</span><span class=\"text_25\"></span><span class=\"text_25\">略</span><span class=\"text_25\"> 队</span><span class=\"text_26\">友反应</span><span class=\"text_25\"></span><span class=\"text_25\">略</span><span class=\"text_26\"></span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_464\"><span class=\"text_\">略</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_61\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">排练重开</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_445\"><span class=\"text_\">指挥进入起拍 乐队跟进</span></p><p class=\"block_445\"><span class=\"text_\">主角无所事事</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_74\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">若干恶搞环节</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_557\"><span class=\"text_\">主角干扰众乐手 主角戏弄首席 主角模仿丑化指挥</span></p></td>\n\t\t\t<td class=\"td_5\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_91\">\n\t\t\t<td class=\"td_10\"><p class=\"block_145\"><span class=\"text_\">乐谱问题</span></p></td>\n\t\t\t<td class=\"td_10\"><p class=\"block_557\"><span class=\"text_\">钢琴开始演奏 主角发现乐谱不对 独奏演员关上乐谱</span></p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
