        "$:/plugins/twpub/c69c7142dcf77b93/text/000002745": {
            "role": "text",
            "title": "$:/plugins/twpub/c69c7142dcf77b93/text/000002745",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c69c7142dcf77b93-2cf4b4 twpub-c69c7142dcf77b93-9882b5\"><table class=\"table_1\">\n\t\t<tbody class=\"calibre11\"><tr class=\"tr_110\">\n\t\t\t<td rowspan=\"2\" class=\"td_3\"><p class=\"block_145\"><span class=\"text_\">人物</span></p></td>\n\t\t\t<td colspan=\"2\" class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">身份</span></p></td>\n\t\t\t<td colspan=\"2\" class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">欲望</span></p></td>\n\t\t\t<td colspan=\"2\" class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">动作</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_31\">\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">起点</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">终点</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">起点</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">终点</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">起点</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_145\"><span class=\"text_\">终点</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_100\">\n\t\t\t<td class=\"td_3\"><p class=\"block_19\"><span class=\"text_\">张弓</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">乐团弱者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_238\"><span class=\"text_\">有尊严的 艺术家</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">以公谋私 得过且过</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_238\"><span class=\"text_\">捍卫尊 严发泄 愤懑</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_238\"><span class=\"text_\">敷衍工作 恶搞同事</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_829\"><span class=\"text_22\">率众抗争</span><span class=\"text_22\"> </span><span class=\"text_22\">恶搞音乐 会</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_125\">\n\t\t\t<td class=\"td_3\"><p class=\"block_211\"><span class=\"text_\">李芮蕾</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_233\"><span class=\"text_\">艺术爱好 者</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_645\"><span class=\"text_\">有尊严的 艺术家</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">投身纯粹 艺术</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_830\"><span class=\"text_\">捍卫艺术 尊严</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">追求张弓</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">支持张弓</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_55\">\n\t\t\t<td rowspan=\"2\" class=\"td_3\"><p class=\"block_211\"><span class=\"text_\">朱方正</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">有尊严的</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">崩溃了的</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">投身纯粹</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">守卫个人</span></p></td>\n\t\t\t<td rowspan=\"2\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">专注工作</span></p></td>\n\t\t\t<td rowspan=\"2\" class=\"td_4\"><p class=\"block_211\"><span class=\"text_\">愤然离开</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_42\">\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">艺术家</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">艺术家</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">音乐工作</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">尊严</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_54\">\n\t\t\t<td rowspan=\"2\" class=\"td_3\"><p class=\"block_145\"><span class=\"text_\">钟纾雅</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">大气的艺</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">小气的晚</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">拉拢张弓</span><span class=\"text_22\"></span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">打击张弓</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_19\"><span class=\"text_22\">支持张弓</span><span class=\"text_22\"></span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">利用权力</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_10\">\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">术商人</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">会导演</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">合作赚钱</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">泄私愤</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">相互利用</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">报复张弓</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_54\">\n\t\t\t<td rowspan=\"2\" class=\"td_3\"><p class=\"block_145\"><span class=\"text_\">陈朝辉</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_427\"><span class=\"text_\">不安现状</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_211\"><span class=\"text_\">有尊严的</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_391\"><span class=\"text_22\">倚靠张弓</span><span class=\"text_22\"></span></p></td>\n\t\t\t<td rowspan=\"2\" class=\"td_4\"><p class=\"block_427\"><span class=\"text_\">捍卫尊严</span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_19\"><span class=\"text_22\">争取机会</span><span class=\"text_22\"></span></p></td>\n\t\t\t<td class=\"td_9\"><p class=\"block_19\"><span class=\"text_22\">支持张弓</span><span class=\"text_22\"></span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_42\">\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">的乐手</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">艺术家</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_427\"><span class=\"text_\">私人赚钱</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">不务正业</span></p></td>\n\t\t\t<td class=\"td_15\"><p class=\"block_211\"><span class=\"text_\">共同反击</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_127\">\n\t\t\t<td class=\"td_3\"><p class=\"block_211\"><span class=\"text_\">汪一波</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_481\"><span class=\"text_22\">四面楚 </span><span class=\"text_22\">歌负面 网红</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_445\"><span class=\"text_\">如日中天 的企业家</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_232\"><span class=\"text_\">寻找花瓶 洗白形象</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_481\"><span class=\"text_22\">控制乐 团达成 </span><span class=\"text_22\">目的</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_645\"><span class=\"text_\">争取与乐 团合作</span></p></td>\n\t\t\t<td class=\"td_4\"><p class=\"block_645\"><span class=\"text_\">强迫乐团 媚俗</span></p></td>\n\t\t</tr>\n\t\t<tr class=\"tr_23\">\n\t\t\t<td class=\"td_12\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t\t<td class=\"td_7\"><p class=\"block_28\"> </p></td>\n\t\t</tr>\n\t</tbody></table></div>"
        },
