        "$:/plugins/twpub/c862811230e82e2a/text/000000178": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000178",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_39\"><span class=\"bullet_2\">1 </span><span class=\"calibre10\"><span class=\"text_5\">马克思</span><span class=\"text_5\">.1844</span><span class=\"text_5\">年经济学哲学手稿</span><span class=\"text_5\">[M].</span><span class=\"text_5\">人民岀版社,2014: </span><span class=\"text_5\">49.</span></span></div></div></div>"
        },
