        "$:/plugins/twpub/c862811230e82e2a/text/000000200": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000200",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_49\"><span class=\"bullet_2\">5 </span><span class=\"calibre11\"><span class=\"text_3\">现有的中译本将其译作“推进主义”。见泽拉兹尼胡纾.光明王[岡.北京北京联合岀版公司,2015.</span></span></div></div></div>"
