        "$:/plugins/twpub/c862811230e82e2a/text/000000315": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000315",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_47\"><span class=\"bullet_2\">7 </span><span class=\"calibre11\"><span class=\"text_3\">Andy Beckett. Accelerationism: how a fringe philosophy predicted the future we live in[EB/OL]. (2017-05</span><span class=\"text_3\"></span><span class=\"text_3\">11)[2021-12-22].</span><span class=\"text_3\"><span class=\"tab1\">      </span></span><span class=\"text_3\"></span><a href=\"https://www.theguardian.com/world/2017/may/11/accelerationism-how-a-fringe-philosophy-\" class=\"calibre12\" rel=\"noopener noreferrer\" target=\"_blank\"><span class=\"text_3\">https://www.theguardian.com/world/2017/may/11/accelerationism-how-a-fringe-philosophy-</span></a></span></div></div></div>"
        },
