        },
        "$:/plugins/twpub/c862811230e82e2a/text/000000553": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000553",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_39\"><span class=\"bullet_2\">2 </span><span class=\"calibre10\"><span class=\"text_5\">Land N. Fanged Noumena: Collected Writings 1987—2007[M]. Urbanomic/Sequence Press, 2012: 624.</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000000554": {
            "role": "text",
