        "$:/plugins/twpub/c862811230e82e2a/text/000000768": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000768",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_48\"><span class=\"bullet_2\">2 </span><span class=\"calibre11\"><span class=\"text_3\">德勒兹.尼采与哲学</span><span class=\"text_3\">[M].</span><span class=\"text_3\">社会科学文献岀版社,2001: </span><span class=\"text_3\">265-267.</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000000769": {
