        "$:/plugins/twpub/c862811230e82e2a/text/000000961": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000961",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_86\"><span class=\"bullet_2\">5 </span><span class=\"calibre10\"><span class=\"text_5\">Land N. The Thirst for Annihilation: Georges Bataille and Virulent Nihilism[M]. Routledge, </span><span class=\"text_5\">1992: 3.</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000000962": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000000962",
