        "$:/plugins/twpub/c862811230e82e2a/text/000001072": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001072",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_39\"><span class=\"bullet_2\">4 </span><span class=\"calibre10\"><span class=\"text_5\">尼采,</span><span class=\"text_5\">et al.</span><span class=\"text_5\">瞧这个人尼采自传</span><span class=\"text_5\">[M].</span><span class=\"text_5\">团结岀版社,2006: </span><span class=\"text_5\">76.</span></span></div></div></div>"
