        "$:/plugins/twpub/c862811230e82e2a/text/000001355": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001355",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_262\"><span class=\"bullet_2\">2 </span><span class=\"calibre10\"><span class=\"text_5\">巴塔耶刘云虹胡陈尧.被诅咒的部分[M].南京南京大学岀版社,2019: 122-123.</span></span></div></div></div>"
        },
