        },
        "$:/plugins/twpub/c862811230e82e2a/text/000001381": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001381",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_282\"><span class=\"bullet_3\">5 </span><span class=\"calibre10\"><span class=\"text_16\">巴塔耶董澄波.文学与恶[M].北京北京燕山岀版社,2006: 157. </span><span class=\"text_4\">文学与夸富宴、与阿兹特克人血腥的人牲献祭是同一回事使人在对毁灭的直观 中迷失自己、打破自己的界限、融入内在共通的激情。</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000001382": {
            "role": "text",
