        "$:/plugins/twpub/c862811230e82e2a/text/000001517": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001517",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_131\"><span class=\"bullet_2\">3 </span><span class=\"calibre10\"><span class=\"text_5\">巴塔耶程小牧.内在经验[M].北京生活•读书•新知三联书店,2017: 170.</span></span></div></div></div>"
