        "$:/plugins/twpub/c862811230e82e2a/text/000001850": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001850",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_47\"><span class=\"bullet_2\">5 </span><span class=\"calibre11\"><span class=\"text_3\">有研究者从康德式的先验时间与德勒兹的时间的三重综合的角度来理解兰德的时间观念见忆子. CCRU</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000001851": {
