        },
        "$:/plugins/twpub/c862811230e82e2a/text/000001877": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001877",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_161\"><span class=\"bullet_2\">2 </span><span class=\"calibre10\"><span class=\"text_5\">Mackay R, Avanessian A. # Accelerate: The Accelerationist Reader[C]. Urbanomic, 2014: 519.</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000001878": {
