        "$:/plugins/twpub/c862811230e82e2a/text/000001919": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000001919",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_131\"><span class=\"bullet_2\">2 </span><span class=\"calibre10\"><span class=\"text_5\">巴塔耶刘晖.色情史</span><span class=\"text_5\">[M].</span><span class=\"text_5\">北京商务印书馆,2003: </span><span class=\"text_5\">88.</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000001920": {
