        },
        "$:/plugins/twpub/c862811230e82e2a/text/000002274": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000002274",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_48\"><span class=\"bullet_2\">2 </span><span class=\"calibre11\"><span class=\"text_3\">Noys B. Malign velocities: Accelerationism and capitalism[M]. Zero Books, </span><span class=\"text_3\">2014: </span><span class=\"text_3\">Conclusion: The Moving Contradiction.</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000002275": {
