        "$:/plugins/twpub/c862811230e82e2a/text/000002292": {
            "role": "text",
            "title": "$:/plugins/twpub/c862811230e82e2a/text/000002292",
            "type": "text/vnd.twpub",
            "text": "<div class=\"twpub-c862811230e82e2a-c8b42a twpub-c862811230e82e2a-9882b5\"><div class=\"calibre9\"><div class=\"block_48\"><span class=\"bullet_2\">2 </span><span class=\"calibre11\"><span class=\"text_3\">Mencius Moldbug</span><span class=\"text_3\">本名</span><span class=\"text_3\">Curtis Yarvin</span><span class=\"text_3\">是活跃在互联网上的企业家、右翼思想家。</span></span></div></div></div>"
        },
        "$:/plugins/twpub/c862811230e82e2a/text/000002293": {
